import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { Navigate } from "react-router-dom";

function Settings(props) {
  return (
    <div
      className="d-flex upperture-bg-medium-grey"
      style={{ display: "block", overflow: "auto", minHeight: "100vh" }}
    >
      <Navpane />
      <div
        className="page-container detail-page-container justify-content-around"
        style={{ padding: "3vw" }}
      >
        <div id="settings-header-container">
          <h1>Settings</h1>
        </div>
        <div id="settings-container">
          <p className="m-0 upperture-main-dark">{`Email: ${email}`}</p>
          <p className="mt-0 mb-4 upperture-dark-grey upperture-font-size-14">
            Email can not be changed
          </p>
          <p className="mt-0 mb-4 upperture-main-dark">
            Account status: verified
          </p>
          <p className="mt-4 mb-0 upperture-main-dark">Change password</p>
          <div
            className="upperture-form"
            style={{ margin: "10px 0px 20px 0px" }}
          >
            <input
              className="upperture-input"
              type={seeOldPassword ? "text" : "password"}
              placeholder=" "
              id="oldPassword"
              maxLength={160}
              onChange={oldPasswordChange}
              value={oldPassword}
            />
            <label className="upperture-input-label" htmlFor="oldPassword">
              Old password
            </label>
            {seeOldPassword ? (
              <img
                className="upperture-password-icon upperture-pointer"
                src={HidePassword}
                alt="hide"
                onClick={() => setSeeOldPassword(false)}
              />
            ) : (
              <img
                className="upperture-password-icon upperture-pointer"
                src={ShowPassword}
                alt="show"
                onClick={() => setSeeOldPassword(true)}
              />
            )}
            {oldPasswordErrorMsg && oldPassword && (
              <p
                style={{
                  margin: "0px 20px",
                  color: "#f0547b",
                  textAlign: "center",
                  fontSize: "12px",
                }}
              >
                {oldPasswordErrorMsg}
              </p>
            )}
          </div>
          <div className="upperture-form" style={{ margin: "20px 0px" }}>
            <input
              className="upperture-input"
              type={seeNewPassword ? "text" : "password"}
              placeholder=" "
              id="newPassword"
              maxLength={160}
              onChange={newPasswordChange}
              value={newPassword}
            />
            <label className="upperture-input-label" htmlFor="newPassword">
              New password
            </label>
            {seeNewPassword ? (
              <img
                className="upperture-password-icon upperture-pointer"
                src={HidePassword}
                alt="hide"
                onClick={() => setSeeNewPassword(false)}
              />
            ) : (
              <img
                className="upperture-password-icon upperture-pointer"
                src={ShowPassword}
                alt="show"
                onClick={() => setSeeNewPassword(true)}
              />
            )}
            {newPasswordErrorMsg && newPassword && (
              <p
                style={{
                  margin: "0px 20px",
                  color: "#f0547b",
                  textAlign: "center",
                  fontSize: "12px",
                }}
              >
                {newPasswordErrorMsg}
              </p>
            )}
          </div>
          <div className="upperture-form" style={{ margin: "20px 0px" }}>
            <input
              className="upperture-input"
              type={seeRepeatNewPassword ? "text" : "password"}
              placeholder=" "
              id="repeatNewPassword"
              onChange={repeatNewPasswordChange}
              maxLength={160}
              value={repeatNewPassword}
            />
            <label
              className="upperture-input-label"
              htmlFor="repeatNewPassword"
            >
              Repeat new password
            </label>
            {seeRepeatNewPassword ? (
              <img
                className="upperture-password-icon upperture-pointer"
                src={HidePassword}
                alt="hide"
                onClick={() => setSeeRepeatNewPassword(false)}
              />
            ) : (
              <img
                className="upperture-password-icon upperture-pointer"
                src={ShowPassword}
                alt="show"
                onClick={() => setSeeRepeatNewPassword(true)}
              />
            )}
            {repeatNewPasswordErrorMsg && repeatNewPassword && (
              <p
                style={{
                  margin: "0px 20px",
                  color: "#f0547b",
                  textAlign: "center",
                  fontSize: "12px",
                }}
              >
                {repeatNewPasswordErrorMsg}
              </p>
            )}
          </div>
          <p className="upperture-font-size-12 upperture-dark-grey mb-4">
            Password should contain at least 8 characters including an uppercase
            letter, a lowercase letter, a symbol, and a number
          </p>
          <button
            className="upperture-submit-button"
            onClick={() => {
              onSubmit();
              setOldPassword("");
              setNewPassword("");
              setRepeatNewPassword("");
            }}
            disabled={
              oldPasswordErrorMsg ||
              newPasswordErrorMsg ||
              repeatNewPasswordErrorMsg ||
              !(oldPassword && newPassword && repeatNewPassword) ||
              isSubmitting
            }
          >
            Save
          </button>
        </div>
      </div>
    </div>
  );
}

export default Settings;
